def PredictionGraph():
    import pandas as pd
    import numpy as np
    import matplotlib.pyplot as plt
    import matplotlib
    from sklearn.preprocessing import MinMaxScaler
    from keras.layers import LSTM,Dense,Dropout
    from sklearn.model_selection import TimeSeriesSplit
    from sklearn.metrics import mean_squared_error, r2_score
    import matplotlib.dates as mdates
    from sklearn import linear_model

    sName = str(v.get())
    df_final = pd.read_csv("D:/"+sName + '.csv',na_values=['null'],index_col='Date',parse_dates=True,infer_datetime_format=True)
    #if(sName=='MSFT')df_final = pd.read_csv("D:/MSFT.csv",na_values=['null'],index_col='Date',parse_dates=True,infer_datetime_format=True)
    X=df_final.drop(['Adj Close'],axis=1)
    X=X.drop(['Close'],axis=1)

    test = df_final
# Target column
    target_adj_close = pd.DataFrame(test['Adj Close'])

    feature_columns = ['Open', 'High', 'Low', 'Volume']

    from sklearn.preprocessing import MinMaxScaler
    scaler = MinMaxScaler()
    feature_minmax_transform_data = scaler.fit_transform(test[feature_columns])
    feature_minmax_transform = pd.DataFrame(columns=feature_columns, data=feature_minmax_transform_data, index=test.index)

    target_adj_close = target_adj_close.shift(-1)
    validation_y = target_adj_close[-90:-1]
    target_adj_close = target_adj_close[:-90]

# Taking last 90 rows of data to be validation set
    validation_X = feature_minmax_transform[-90:-1]
    feature_minmax_transform = feature_minmax_transform[:-90]

    s_split= TimeSeriesSplit(n_splits=10)
    for train_index, test_index in s_split.split(feature_minmax_transform):
        X_train, X_test = feature_minmax_transform[:len(train_index)], feature_minmax_transform[len(train_index): (len(train_index)+len(test_index))]
        y_train, y_test = target_adj_close[:len(train_index)].values.ravel(), target_adj_close[len(train_index): (len(train_index)+len(test_index))].values.ravel()
        
    def validate_result(model, model_name):
        predicted = model.predict(validation_X)
        RSME_score = np.sqrt(mean_squared_error(validation_y, predicted))
        print('RMSE: ', RSME_score)
    
        R2_score = r2_score(validation_y, predicted)
        print('R2 score: ', R2_score)

        plt.plot(validation_y.index, predicted,'r', label='Predict')
        plt.plot(validation_y.index, validation_y,'b', label='Actual')
        plt.ylabel('Price')
        plt.gca().xaxis.set_major_formatter(mdates.DateFormatter('%Y-%m-%d'))
        plt.gca().xaxis.set_major_locator(mdates.MonthLocator())
        plt.title(model_name + ' Predict vs Actual')
        plt.legend(loc='upper right')
        plt.show()
    
    X_train =np.array(X_train)
    X_test =np.array(X_test)

    X_tr_t = X_train.reshape(X_train.shape[0], 1, X_train.shape[1])
    X_tst_t = X_test.reshape(X_test.shape[0], 1, X_test.shape[1])

    from keras.models import Sequential
    from keras.layers import Dense
    import keras.backend as K
    from keras.callbacks import EarlyStopping
    from keras.optimizers import Adam
    from keras.models import load_model
    from keras.layers import LSTM
    K.clear_session()
    model_lstm = Sequential()
    model_lstm.add(LSTM(16, input_shape=(1, X_train.shape[1]), activation='relu', return_sequences=False))
    model_lstm.add(Dense(1))
    model_lstm.compile(loss='mean_squared_error', optimizer='adam')
    early_stop = EarlyStopping(monitor='loss', patience=5, verbose=1)
    history_model_lstm = model_lstm.fit(X_tr_t, y_train, epochs=100, batch_size=8, verbose=1, shuffle=False, callbacks=[early_stop])

    y_pred_test_lstm = model_lstm.predict(X_tst_t)
    y_train_pred_lstm = model_lstm.predict(X_tr_t)
    print("The R2 score on the Train set is:\t{:0.3f}".format(r2_score(y_train, y_train_pred_lstm)))
    r2_train = r2_score(y_train, y_train_pred_lstm)

    print("The R2 score on the Test set is:\t{:0.3f}".format(r2_score(y_test, y_pred_test_lstm)))
    r2_test = r2_score(y_test, y_pred_test_lstm)
    score_lstm= model_lstm.evaluate(X_tst_t, y_test, batch_size=1)

    fig = Figure(figsize=(6,3.5))
    plt = fig.add_subplot(111)


    y_pred_test_LSTM = model_lstm.predict(X_tst_t)
    plt.plot(y_test, label='True Value')
    plt.plot(y_pred_test_LSTM + 10, label='LSTM (Predicted)')
    plt.set_xlabel('Observation')
    plt.set_ylabel('INR_Scaled')

    canvas8 = Canvas(root, width=6, height=4)
    canvas8 = FigureCanvasTkAgg(fig, master=root)
    canvas8.get_tk_widget().place(x=30,y=260) 
    canvas8.draw()

